select.where:
1=1
 and id = #{id}
 and workorder_name = #{workorderName}

    and workorder_number like concat('%', #{workorderNumber}, '%')

 and company_id = #{companyId}
 and line_id = #{lineId}
 and product_name = #{productName}
 and product_code = #{productCode}
 and order_code like concat(#{orderCode}, '%')
 and ecn_status = #{ecnStatus}
 and product_standard_hour = #{productStandardHour}
 and create_time = #{createTime}
 and create_by = #{createBy}
 and update_time = #{updateTime}
 and device_liable = #{deviceLiable}
 and update_by = #{updateBy}
 and people_number = #{peopleNumber}
 and working_hour = #{workingHour}
 and overtime_hour = #{overtimeHour}
 and overtime_race = #{overtimeRace}
 and manual_time = #{manualTime}
 and workorder_status = #{workorderStatus}
 and operation_status = #{operationStatus}
 and init_date = #{initDate}
 and work_sign = #{workSign}
 and scrap_num = #{scrapNum}
 and actual_warehouse_num = #{actualWarehouseNum}

    AND date_format(production_start,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(production_end,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

mapper.update:
update dev_work_order
workorder_name = #{workorderName}, workorder_number = #{workorderNumber}, company_id = #{companyId}, line_id = #{lineId}, product_name = #{productName}, product_code = #{productCode}, product_number = #{productNumber}, production_start = #{productionStart}, production_end = #{productionEnd}, start_time = #{startTime}, end_time = #{endTime}, product_standard_hour = #{productStandardHour}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, device_liable = #{deviceLiable}, update_by = #{updateBy}, people_number = #{peopleNumber}, working_hour = #{workingHour}, overtime_hour = #{overtimeHour}, overtime_race = #{overtimeRace}, manual_time = #{manualTime}, workorder_status = #{workorderStatus}, operation_status = #{operationStatus}, init_date = #{initDate}, work_sign = #{workSign}, scrap_num = #{scrapNum}, actual_warehouse_num = #{actualWarehouseNum}, bad_number = #{badNumber}, cumulative_number = #{cumulativeNumber}, direct_pass_rate = #{directPassRate}, paramConfig = #{paramConfig}, remark = #{remark}, order_code =#{orderCode}, ecn_status =#{ecnStatus}, ecn_text =#{ecnText}, update_time = sysdate()
where id = #{id}

mapper.select:
SELECT * FROM dev_work_order where company_id = #{company_id}

    and line_id = #{line_id}


    and product_code = #{productCode}

 and work_sign = 1 and end_time between #{startTime} and #{endTime} ;


where workorder_status in (1,2)

    and company_id = #{companyId}

and start_time BETWEEN DATE_FORMAT(NOW(),'%Y-%m-%d 00:00:00')  AND DATE_FORMAT(NOW(),'%Y-%m-%d 23:59:59')


where company_id = #{companyId}

    and id = #{workOrderId}

and line_id = #{lineId} and work_sign = 1 and actual_warehouse_num < product_number;
